/* ---------- FASE VELOCIDADE ---------- */
function faseVelocidade(){
let s0=+document.getElementById("s0").value;
let v=+document.getElementById("v").value;
let t=+document.getElementById("t").value;

let tempo=[], posicao=[];
for(let i=0;i<=t;i++){
tempo.push(i);
posicao.push(s0+v*i);
}

document.getElementById("rv").style.display="block";
document.getElementById("rv").innerHTML=
`üìà Movimento uniforme: posi√ß√£o cresce linearmente com o tempo.`;

pontos+=10;
atualizar();

if(graficoST) graficoST.destroy();
graficoST=new Chart(document.getElementById("graficoST"),{
type:'line',
data:{
labels:tempo,
datasets:[{
label:'Posi√ß√£o (m)',
data:posicao,
borderWidth:2
}]
},
options:{
plugins:{
title:{
display:true,
text:'Gr√°fico Posi√ß√£o √ó Tempo (MRU)'
}
},
scales:{
x:{title:{display:true,text:'Tempo (s)'}},
y:{title:{display:true,text:'Posi√ß√£o (m)'}}
}
}
});
}

/* ---------- FASE ACELERA√á√ÉO ---------- */
function faseAceleracao(){
let v0=+document.getElementById("v0").value;
let a=+document.getElementById("a").value;
let t=+document.getElementById("t2").value;

let tempo=[], vel=[];
for(let i=0;i<=t;i++){
tempo.push(i);
vel.push(v0+a*i);
}

document.getElementById("ra").style.display="block";
document.getElementById("ra").innerHTML=
`‚ö° Velocidade varia linearmente ‚Äî acelera√ß√£o constante.`;

pontos+=10;
